  console.log(count);
  console.log(setCount);

  function increment() {
    setCount(count + 1);
  }

  function decrement() {
    setCount(count - 1);
  }
  return (
    <div className="App">
      <h1>Counter: {count}</h1>
      <h1>Text: {count}</h1>
      <input type="text" value={str}></input>
      <button onClick={increment}>Increment</button>
      <button onClick={decrement}>Decrement</button>
    </div>
  );
}

export default App;
