import React from 'react';

function App() {
  let counter = 0;

  function increment() {
    counter += 1;
    console.log(counter);
  }
  return (
    <div className="App">
      <h1>Counter: {counter}</h1>
      <button onClick={increment}>Increment</button>
      <button onclick={() => counter -= 0}>Decrement</button>
    </div>
  );
}

export default App;
